    public ExampleSpringService(ScriptFactoryPostProcessor scriptFactoryPostProcessor) {
        this.applicationContext = null;
        this.scriptFactoryPostProcessor = scriptFactoryPostProcessor;
    }

    public void destroyScriptFactories() {
