import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import java.util.Map;
import java.util.Set;
import org.springframework.util.LinkedCaseInsensitiveMap;
import java.lang.reflect.Field;

public class ExampleSpringServiceTest {

    @Test
    public void testGetSettingsEntriesEmpty() throws Exception {
        ExampleSpringService service = new ExampleSpringService();
        Set<Map.Entry<String, String>> entries = service.getSettingsEntries();
        assertNotNull(entries, "Entries should not be null");
        assertTrue(entries.isEmpty(), "Entries should be empty initially");
    }

    @Test
    public void testGetSettingsEntriesCaseInsensitive() throws Exception {
        ExampleSpringService service = new ExampleSpringService();

        // Access the private settingsMap using reflection
        Field settingsMapField = ExampleSpringService.class.getDeclaredField("settingsMap");
        settingsMapField.setAccessible(true);
        @SuppressWarnings("unchecked")
        LinkedCaseInsensitiveMap<String> settingsMap = (LinkedCaseInsensitiveMap<String>) settingsMapField.get(service);

        // Add entries with different cases
        settingsMap.put("SettingKey", "Value1");
        settingsMap.put("settingkey", "Value2"); // Should overwrite the previous value

        Set<Map.Entry<String, String>> entries = service.getSettingsEntries();
        assertEquals(1, entries.size(), "There should be only one entry due to case-insensitive keys");

        Map.Entry<String, String> entry = entries.iterator().next();
        // Updated assertion to check key case-insensitively
        assertTrue(entry.getKey().equalsIgnoreCase("SettingKey"), "Key should be case-insensitive");
        assertEquals("Value2", entry.getValue(), "Value should be updated to the latest entry");
    }

    @Test
    public void testGetSettingsEntriesOrdered() throws Exception {
        ExampleSpringService service = new ExampleSpringService();

        // Access the private settingsMap using reflection
        Field settingsMapField = ExampleSpringService.class.getDeclaredField("settingsMap");
        settingsMapField.setAccessible(true);
        @SuppressWarnings("unchecked")
        LinkedCaseInsensitiveMap<String> settingsMap = (LinkedCaseInsensitiveMap<String>) settingsMapField.get(service);

        // Add multiple entries
        settingsMap.put("Key1", "Value1");
        settingsMap.put("Key2", "Value2");
        settingsMap.put("Key3", "Value3");

        Set<Map.Entry<String, String>> entries = service.getSettingsEntries();
        assertEquals(3, entries.size(), "There should be three entries");

        String[] expectedKeys = {"Key1", "Key2", "Key3"};
        String[] expectedValues = {"Value1", "Value2", "Value3"};

        int index = 0;
        for (Map.Entry<String, String> entry : entries) {
            assertEquals(expectedKeys[index], entry.getKey(), "Keys should be in insertion order");
            assertEquals(expectedValues[index], entry.getValue(), "Values should be in insertion order");
            index++;
        }
    }
}

