import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import org.springframework.core.io.buffer.DataBuffer;
import org.springframework.core.io.buffer.JettyDataBufferFactory;
import org.springframework.core.io.buffer.DefaultDataBufferFactory;

public class ExampleSpringServiceTest {

    private final ExampleSpringService service = new ExampleSpringService();
    private final DefaultDataBufferFactory dataBufferFactory = new DefaultDataBufferFactory();
    private final JettyDataBufferFactory jettyDataBufferFactory = new JettyDataBufferFactory(true);

    @Test
    public void testWriteByteWithJettyDataBuffer() {
        // Arrange
        DataBuffer jettyBuffer = jettyDataBufferFactory.allocateBuffer();
        byte byteToWrite = 0x2B;

        // Act
        DataBuffer result = service.writeByte(jettyBuffer, byteToWrite);

        // Assert
        byte[] expected = new byte[] { byteToWrite };
        byte[] actual = new byte[result.readableByteCount()];
        result.read(actual);
        assertArrayEquals(expected, actual, "The byte should be correctly written to the JettyDataBuffer");
    }

    @Test
    public void testWriteByteWithUnsupportedDataBuffer() {
        // Arrange
        DataBuffer unsupportedBuffer = dataBufferFactory.allocateBuffer();
        byte byteToWrite = 0x2B;

        // Act & Assert
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            service.writeByte(unsupportedBuffer, byteToWrite);
        });
        assertEquals("Unsupported DataBuffer implementation", exception.getMessage(), 
                     "Should throw IllegalArgumentException for unsupported DataBuffer");
    }
}

