    public void testUpdateConfiguration_UpdateExistingKey_SameCase() {
        String key = "timeout";
        String initialValue = "30";
        String updatedValue = "60";

        service.updateConfiguration(key, initialValue);
        String previousValue = service.updateConfiguration(key, updatedValue);

        assertEquals(initialValue, previousValue, "Previous value should match the initial value.");
        assertEquals(updatedValue, getConfiguration(key), "Current value should be the updated value.");
    }

    @Test
    public void testUpdateConfiguration_UpdateExistingKey_DifferentCase() {
        String keyOriginal = "MaxConnections";
        String keyDifferentCase = "maxconnections";
        String initialValue = "100";
        String updatedValue = "150";

        service.updateConfiguration(keyOriginal, initialValue);
        String previousValue = service.updateConfiguration(keyDifferentCase, updatedValue);

        assertEquals(initialValue, previousValue, "Previous value should match the initial value despite case difference.");
        assertEquals(updatedValue, getConfiguration(keyOriginal), "Current value should be updated regardless of key case.");
    }

    @Test
    public void testUpdateConfiguration_NullValue() {
        String key = "feature.enabled";
        String initialValue = "true";

        service.updateConfiguration(key, initialValue);
        String previousValue = service.updateConfiguration(key, null);

