        String updatedValue = "updatedEmptyKeyTest";
        String prev = service.updateConfiguration(key, updatedValue);
        assertEquals(value, prev, "Previous value should match the initial value for empty key.");
        assertEquals(updatedValue, getConfiguration(key), "Current value should be the updated value for empty key.");
