import java.io.StringWriter;

public class ExampleSpringService {
    /**
     * Writer that provides automatic indentation and line breaks.
     */
    public static class IndentingWriter extends Writer {
        private final StringWriter out;
        private int indentLevel = 0;
        private static final String INDENT = "    ";
        private boolean prependIndent = true;

        public IndentingWriter(StringWriter out) {
            this.out = out;
        }

        public IndentingWriter indent() {
            indentLevel++;
            return this;
        }

        public IndentingWriter unindent() {
            if (indentLevel > 0) {
                indentLevel--;
            }
            return this;
        }

        /**
         * Write a new line.
         */
        public IndentingWriter println() {
            String separator = System.lineSeparator();
            this.out.write(separator.toCharArray(), 0, separator.length());
            this.prependIndent = true;
            return this;
        }

        @Override
        public void write(char[] cbuf, int off, int len) throws IOException {
            for (int i = off; i < off + len; i++) {
                write(cbuf[i]);
            }
        }

        @Override
        public void write(int c) throws IOException {
            if (prependIndent) {
                for (int i = 0; i < indentLevel; i++) {
                    out.write(INDENT);
                }
                prependIndent = false;
            }
            out.write(c);
        }

        @Override
        public void write(String str, int off, int len) throws IOException {
            int start = off;
            int end = off + len;
            while (start < end) {
                int nl = str.indexOf('\n', start);
                if (nl == -1 || nl >= end) {
                    if (prependIndent) {
                        for (int i = 0; i < indentLevel; i++) {
                            out.write(INDENT);
                        }
                        prependIndent = false;
                    }
                    out.write(str, start, end - start);
                    break;
                } else {
                    if (prependIndent) {
                        for (int i = 0; i < indentLevel; i++) {
                            out.write(INDENT);
                        }
                        prependIndent = false;
                    }
                    out.write(str, start, nl - start + 1);
                    prependIndent = true;
                    start = nl + 1;
                }
            }
        }

        @Override
        public void flush() throws IOException {
            out.flush();
        }

        @Override
        public void close() throws IOException {
            out.close();
        }
    }

    /**
     * Factory for IndentingWriter.
     */
    public Writer createIndentedOutputWriter() {
        return new IndentingWriter(new StringWriter());
    }
}

