        int id = service.generateUniqueMessageId();
        assertEquals(1, id, "The first generated ID should be 1");
    }

    @Test
    public void testSequentialIds() {
        ExampleSpringService service = new ExampleSpringService();
        assertEquals(1, service.generateUniqueMessageId(), "The first generated ID should be 1");
        assertEquals(2, service.generateUniqueMessageId(), "The second generated ID should be 2");
        assertEquals(3, service.generateUniqueMessageId(), "The third generated ID should be 3");
    }

    @Test
    public void testThreadSafety() throws InterruptedException, ExecutionException {
        final ExampleSpringService service = new ExampleSpringService();
        int numberOfThreads = 100;
        ExecutorService executor = Executors.newFixedThreadPool(10);
        List<Callable<Integer>> tasks = new ArrayList<>();

        for(int i = 0; i < numberOfThreads; i++) {
            tasks.add(() -> service.generateUniqueMessageId());
        }

        List<Future<Integer>> futures = executor.invokeAll(tasks);
        executor.shutdown();
        executor.awaitTermination(1, TimeUnit.MINUTES);

        Set<Integer> uniqueIds = new HashSet<>();
        for(Future<Integer> future : futures) {
            uniqueIds.add(future.get());
        }

        assertEquals(numberOfThreads, uniqueIds.size(), "All generated IDs should be unique");

        for(int i = 1; i <= numberOfThreads; i++) {
            assertTrue(uniqueIds.contains(i), "ID " + i + " should be present");
        }
    }
}

