import java.lang.reflect.Method;
import java.util.Optional;
import java.util.concurrent.ConcurrentHashMap;
import java.util.Map;

public class ExampleSpringService {
    // Cache for performance and thread safety
    private static final Map<String, Optional<Method>> setterCache = new ConcurrentHashMap<>();

    protected Optional<Method> locateSetter(String propertyName, Class<?> targetClass) {
        if (propertyName == null) throw new NullPointerException("propertyName must not be null");
        if (targetClass == null) throw new NullPointerException("targetClass must not be null");
        final String cacheKey = targetClass.getName() + "#" + propertyName.toLowerCase();
        return setterCache.computeIfAbsent(cacheKey, k -> findSetter(propertyName, targetClass));
    }

    private static Optional<Method> findSetter(String propertyName, Class<?> targetClass) {
        String setterName = "set" + capitalize(propertyName);

        Class<?> current = targetClass;
        while (current != null && current != Object.class) {
            for (Method method : current.getDeclaredMethods()) {
                if (method.getName().equals(setterName) && method.getParameterCount() == 1) {
                    return Optional.of(method);
                }
            }
            current = current.getSuperclass();
        }
        return Optional.empty();
    }

    private static String capitalize(String name) {
        if (name == null || name.isEmpty()) return name;
        if (name.length() > 1 && Character.isUpperCase(name.charAt(1)) && Character.isUpperCase(name.charAt(0)))
            return name; // "URL" remains "URL" (for JavaBeans conventions)
        return Character.toUpperCase(name.charAt(0)) + name.substring(1);
    }
}

