        int actualHashCode = service.computeObjectHash();
        assertEquals(expectedHashCode, actualHashCode, "The computeObjectHash should return the expected hash code.");
    }

    @Test
    public void testComputeObjectHash_IsConsistent() {
        ExampleSpringService service = new ExampleSpringService();
        int firstHash = service.computeObjectHash();
        for (int i = 0; i < 10; i++) {
            int subsequentHash = service.computeObjectHash();
            assertEquals(firstHash, subsequentHash, "computeObjectHash should return consistent hash codes across multiple calls.");
        }
    }

    @Test
    public void testComputeObjectHash_IsThreadSafe() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int expectedHash = service.computeObjectHash();

        int threadCount = 10;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        Callable<Integer> task = service::computeObjectHash;

        Future<Integer>[] futures = new Future[threadCount];
        for (int i = 0; i < threadCount; i++) {
            futures[i] = executor.submit(task);
        }

        for (int i = 0; i < threadCount; i++) {
            assertEquals(expectedHash, futures[i].get(), "computeObjectHash should return the same hash code when called from multiple threads.");
        }

        executor.shutdown();
    }

    @Test
