public class ExampleSpringService {

    // Dependency interface for subscription handling
    public interface SubscriptionRegistry {
        void registerSubscription(String subscribe);
        void unregisterSubscription(String unsubscribe);
        Object findSubscriptionsInternal(String destination, String message);
    }

    // State holder for registry and the message to be searched
    public interface ServerState {
        SubscriptionRegistry getRegistry();
        String getFindMessage();
    }

    // Encapsulates the details of a subscription lifecycle operation
    public interface SubscriptionRequest {
        String getSubscribe();
        String getUnsubscribe();
        String getFindDestination();
    }

    // Blackhole simulation for consuming results (e.g. for benchmarking)
    public interface Blackhole {
        void consume(Object obj);
    }

    // Simulates load environment, provides a Blackhole
    public interface LoadTester {
        Blackhole getBlackhole();
    }

    // Method demonstrating robust handling of the subscription lifecycle
    public void handleSubscriptionLifecycle(ServerState serverState, SubscriptionRequest request, LoadTester tester) {
        SubscriptionRegistry registry = serverState.getRegistry();
        Blackhole blackhole = tester.getBlackhole();

        // Register subscription
        registry.registerSubscription(request.getSubscribe());

        // Find and consume subscriptions
        blackhole.consume(registry.findSubscriptionsInternal(
                request.getFindDestination(),
                serverState.getFindMessage())
        );

        // Unregister subscription
        registry.unregisterSubscription(request.getUnsubscribe());

        // Find and consume again after unregistration
        blackhole.consume(registry.findSubscriptionsInternal(
                request.getFindDestination(),
                serverState.getFindMessage())
        );
    }
}

