import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import org.springframework.ui.ConcurrentModel;
import java.util.Map;
import java.util.HashMap;

public class ExampleSpringServiceTest {

    @Test
    public void testAddAllAttributesWithNull() {
        ExampleSpringService service = new ExampleSpringService();
        ConcurrentModel model = service.addAllAttributes(null);
        assertTrue(model.isEmpty(), "Model should be empty when adding null attributes.");
    }

    @Test
    public void testAddAllAttributesWithEmptyMap() {
        ExampleSpringService service = new ExampleSpringService();
        ConcurrentModel model = service.addAllAttributes(new HashMap<>());
        assertTrue(model.isEmpty(), "Model should be empty when adding an empty map.");
    }

    @Test
    public void testAddAllAttributesWithValidMap() {
        ExampleSpringService service = new ExampleSpringService();
        Map<String, Object> attributes = new HashMap<>();
        attributes.put("key1", "value1");
        attributes.put("key2", 123);
        ConcurrentModel model = service.addAllAttributes(attributes);
        assertEquals(2, model.size(), "Model should contain two attributes.");
        assertEquals("value1", model.getAttribute("key1"), "Attribute 'key1' should match the expected value.");
        assertEquals(123, model.getAttribute("key2"), "Attribute 'key2' should match the expected value.");
    }

    @Test
    public void testAddAllAttributesDoesNotOverwriteExistingAttributes() {
        ExampleSpringService service = new ExampleSpringService();
        Map<String, Object> initialAttributes = new HashMap<>();
        initialAttributes.put("key1", "initialValue");
        service.addAllAttributes(initialAttributes);

        Map<String, Object> newAttributes = new HashMap<>();
        newAttributes.put("key1", "newValue");
        newAttributes.put("key2", "value2");
        ConcurrentModel model = service.addAllAttributes(newAttributes);

        assertEquals(2, model.size(), "Model should contain two attributes.");
        assertEquals("initialValue", model.getAttribute("key1"), "Existing attribute 'key1' should not be overwritten.");
        assertEquals("value2", model.getAttribute("key2"), "New attribute 'key2' should be added.");
    }

    @Test
    public void testAddAllAttributesThreadSafety() throws InterruptedException {
        ExampleSpringService service = new ExampleSpringService();
        Map<String, Object> attributes1 = new HashMap<>();
        attributes1.put("key1", "value1");
        Map<String, Object> attributes2 = new HashMap<>();
        attributes2.put("key2", "value2");

        Thread thread1 = new Thread(() -> service.addAllAttributes(attributes1));
        Thread thread2 = new Thread(() -> service.addAllAttributes(attributes2));

        thread1.start();
        thread2.start();
        thread1.join();
        thread2.join();

        ConcurrentModel model = service.addAllAttributes(null);
        assertEquals(2, model.size(), "Model should contain two attributes after concurrent additions.");
        assertEquals("value1", model.getAttribute("key1"), "Attribute 'key1' should be present.");
        assertEquals("value2", model.getAttribute("key2"), "Attribute 'key2' should be present.");
    }
}

