public class ExampleSpringService {
    private String username = null;

    /**
     * Extends the current username with the provided suffix.
     * Uses immutable Strings for safety and performance.
     *
     * @param suffix String to append to the username. 
     * @return The new username after appending.
     *
     * @throws NullPointerException if suffix is null and username hasn't been initialized.
     */
    public String extendUserName(String suffix) {
        if (suffix == null && username == null) {
            throw new NullPointerException("Suffix is null and no username is set.");
        }
        if (username == null) {
            username = suffix;
        } else {
            username = username + suffix;
        }
        // If suffix is empty string, append does nothing; if null and username exists, concatenates "null"
        return username;
    }
}

