    }

    @Test
    public void testInitializeSubscriptions_NoSubscribers() throws Exception {
        // Set contention to "noSubscribers"
        setContention("noSubscribers");

        exampleSpringService.initializeSubscriptions(serverState);

        // Access the private destination field
        String destination = getDestination();
        assertTrue(destination.startsWith("someDestination_withNoSubscribers_"));
        int uniqueId = serverState.getUniqueIdGenerator().get();
        assertEquals(uniqueId, extractUniqueId(destination));
    }

    @Test
    public void testInitializeSubscriptions_SameDestination() throws Exception {
        // Set contention to "sameDestination"
        setContention("sameDestination");

        exampleSpringService.initializeSubscriptions(serverState);

        // Access the private destination field
        String destination = getDestination();
        assertEquals("dest1", destination);
    }

    @Test
    public void testInitializeSubscriptions_None() throws Exception {
        // Set contention to "none"
        setContention("none");

        exampleSpringService.initializeSubscriptions(serverState);

        // Access the private destination field
        String destination = getDestination();
        int uniqueNumber = serverState.getUniqueIdGenerator().get() - 1; // because it was incremented after use
        String expectedDestination = serverState.getDestinationIds()[uniqueNumber % serverState.getDestinationIds().length];
        assertEquals(expectedDestination, destination);
    }

    @Test
    public void testInitializeSubscriptions_InvalidContention() throws Exception {
        // Set contention to an invalid value
        setContention("invalidValue");

        Exception exception = assertThrows(IllegalStateException.class, () -> {
            exampleSpringService.initializeSubscriptions(serverState);
        });

        // Verify that the exception message is null
        assertNull(exception.getMessage());
    }

    // Helper method to set the 'contention' field
    private void setContention(String contentionValue) throws Exception {
        Field contentionField = ExampleSpringService.DefaultSubscriptionRegistryBenchmark.class.getDeclaredField("contention");
        contentionField.setAccessible(true);
        contentionField.set(subscriptionBenchmark, contentionValue);
    }

    // Helper method to get the 'destination' field
    private String getDestination() throws Exception {
        Field destinationField = ExampleSpringService.DefaultSubscriptionRegistryBenchmark.class.getDeclaredField("destination");
        destinationField.setAccessible(true);
        return (String) destinationField.get(subscriptionBenchmark);
    }

    // Helper method to extract unique ID from destination string
    private int extractUniqueId(String destination) {
        String[] parts = destination.split("_");
        return Integer.parseInt(parts[parts.length - 1]);
    }
