public class ExampleSpringService {
    public Properties parseStringArrayToProperties(String[] elements, String delimiter) {
        if (delimiter == null || delimiter.isEmpty()) {
            throw new IllegalArgumentException("Delimiter must not be null or empty");
        }
        // Accept null or empty array, return empty Properties in those cases
        if (elements == null || elements.length == 0) {
            return new Properties();
        }
        Properties props = StringUtils.splitArrayElementsIntoProperties(elements, delimiter);
        if (props == null) {
            // StringUtils returns null if the result would be empty
            return new Properties();
        }
        return props;
    }
