import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.util.Set;
import java.util.concurrent.ConcurrentSkipListSet;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class ExampleSpringServiceTest {

    @Test
    public void testIncrementAndGetIdSingleThreaded() {
        ExampleSpringService service = new ExampleSpringService();
        assertEquals(1, service.incrementAndGetId(), "First ID should be 1");
        assertEquals(2, service.incrementAndGetId(), "Second ID should be 2");
        assertEquals(3, service.incrementAndGetId(), "Third ID should be 3");
    }

    @Test
    public void testIncrementAndGetIdMultiThreaded() throws InterruptedException {
        final ExampleSpringService service = new ExampleSpringService();
        final int numberOfThreads = 1000;
        ExecutorService executor = Executors.newFixedThreadPool(10);
        Set<Integer> ids = new ConcurrentSkipListSet<>();

        for (int i = 0; i < numberOfThreads; i++) {
            executor.execute(() -> {
                int id = service.incrementAndGetId();
                ids.add(id);
            });
        }

        executor.shutdown();
        boolean finished = executor.awaitTermination(1, TimeUnit.MINUTES);
        assertTrue(finished, "Executor did not terminate in the specified time");

        assertEquals(numberOfThreads, ids.size(), "All IDs should be unique and exactly " + numberOfThreads + " IDs generated");
        for (int i = 1; i <= numberOfThreads; i++) {
            assertTrue(ids.contains(i), "ID " + i + " should be present");
        }
    }
}

