import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.io.TempDir;
import static org.junit.jupiter.api.Assertions.*;

import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.WritableByteChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.*;

public class ExampleSpringServiceTest {

    private ExampleSpringService service;

    @BeforeEach
    public void setUp() {
        service = new ExampleSpringService();
    }

    @Test
    public void testCreateSafeWriteChannelIsOpen(@TempDir Path tempDir) throws IOException {
        Path filePath = tempDir.resolve("testFile1.txt");
        Files.createFile(filePath);
        WritableByteChannel channel = service.createSafeWriteChannel(filePath);
        assertTrue(channel.isOpen(), "Channel should be open after creation");
        channel.close();
    }

    @Test
    public void testWriteToChannel(@TempDir Path tempDir) throws IOException {
        Path filePath = tempDir.resolve("testFile2.txt");
        Files.createFile(filePath);
        WritableByteChannel channel = service.createSafeWriteChannel(filePath);

        String data = "Hello, World!";
        ByteBuffer buffer = ByteBuffer.wrap(data.getBytes());
        int bytesWritten = channel.write(buffer);
        channel.close();

        assertEquals(data.length(), bytesWritten, "Number of bytes written should match data length");
        String fileContent = Files.readString(filePath);
        assertEquals(data, fileContent, "File content should match written data");
    }

    @Test
    public void testThreadSafety(@TempDir Path tempDir) throws IOException, InterruptedException, ExecutionException {
        Path filePath = tempDir.resolve("testFile3.txt");
        Files.createFile(filePath);
        WritableByteChannel channel = service.createSafeWriteChannel(filePath);

        int threadCount = 10;
        int writesPerThread = 100;
        String data = "ThreadSafeData\n";
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        Callable<Void> writeTask = () -> {
            ByteBuffer buffer = ByteBuffer.wrap(data.getBytes());
            for (int i = 0; i < writesPerThread; i++) {
                channel.write(buffer);
                buffer.rewind();
            }
            return null;
        };

        List<Future<Void>> futures = new ArrayList<>();
        for (int i = 0; i < threadCount; i++) {
            futures.add(executor.submit(writeTask));
        }

        for (Future<Void> future : futures) {
            future.get();
        }

        channel.close();
        executor.shutdown();

        long expectedLines = threadCount * writesPerThread;
        long actualLines = Files.lines(filePath).count();
        assertEquals(expectedLines, actualLines, "All lines should be written without corruption");
    }

    @Test
    public void testCloseChannel(@TempDir Path tempDir) throws IOException {
        Path filePath = tempDir.resolve("testFile4.txt");
        Files.createFile(filePath);
        WritableByteChannel channel = service.createSafeWriteChannel(filePath);
        assertTrue(channel.isOpen(), "Channel should be open before closing");
        channel.close();
        assertFalse(channel.isOpen(), "Channel should be closed after calling close()");
    }

    @Test
    public void testInvalidFilePath() {
        Path invalidPath = Path.of("/invalid/path/testFile5.txt");
        assertThrows(IOException.class, () -> {
            service.createSafeWriteChannel(invalidPath);
        }, "Creating channel with invalid path should throw IOException");
    }
}

