import java.lang.annotation.Annotation;
import java.util.Optional;
import java.util.function.Predicate;
import org.springframework.core.annotation.MergedAnnotation;
import org.springframework.core.annotation.MergedAnnotations;

public class ExampleSpringService {
    public <A extends Annotation> Optional<A> extractConditionalAnnotation(
            Class<A> annotationType,
            Predicate<? super MergedAnnotation<A>> condition) {

        MergedAnnotations annotations = MergedAnnotations.from(this.getClass());
        MergedAnnotation<A> mergedAnnotation = annotations.get(annotationType);
        if (!mergedAnnotation.isPresent()) {
            return Optional.empty();
        }
        return mergedAnnotation.synthesize(condition);
    }
}

