import org.springframework.util.LinkedMultiValueMap;
import org.springframework.util.MultiValueMap;

import java.util.Collections;
import java.util.List;

public class ExampleSpringService {
    // Holds configuration key->list mappings
    private final MultiValueMap<String, Configuration> configMap = new LinkedMultiValueMap<>();

    /**
     * Retrieves an immutable list of configurations for the given key.
     * Returns null if key is null.
     */
    public List<Configuration> fetchConfigurations(String key) {
        if (key == null)
            return null;
        List<Configuration> configs = configMap.get(key);
        if (configs == null) {
            return Collections.emptyList();
        }
        return Collections.unmodifiableList(configs);
    }
}

