import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

import java.util.Set;
import java.util.HashSet;
import java.util.concurrent.*;
import java.util.stream.Collectors;
import java.util.stream.IntStream;

public class ExampleSpringServiceTest {

    @Test
    public void testGenerateUniqueIdSequential() {
        ExampleSpringService service = new ExampleSpringService();
        int numberOfIds = 100;
        Set<String> uniqueIds = new HashSet<>();

        for (int i = 1; i <= numberOfIds; i++) {
            String id = service.generateUniqueId();
            assertNotNull(id, "Generated ID should not be null");
            assertEquals(String.valueOf(i), id, "ID should be sequential and match the expected value");
            boolean isUnique = uniqueIds.add(id);
            assertTrue(isUnique, "ID should be unique");
        }

        assertEquals(numberOfIds, uniqueIds.size(), "All generated IDs should be unique");
    }

    @Test
    public void testGenerateUniqueIdConcurrent() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int numberOfThreads = 50;
        int idsPerThread = 100;
        ExecutorService executor = Executors.newFixedThreadPool(numberOfThreads);
        Set<Future<Set<String>>> futures = new HashSet<>();

        for (int i = 0; i < numberOfThreads; i++) {
            futures.add(executor.submit(() -> {
                Set<String> threadIds = new HashSet<>();
                for (int j = 0; j < idsPerThread; j++) {
                    String id = service.generateUniqueId();
                    assertNotNull(id, "Generated ID should not be null");
                    threadIds.add(id);
                }
                return threadIds;
            }));
        }

        executor.shutdown();
        assertTrue(executor.awaitTermination(1, TimeUnit.MINUTES), "Executor did not terminate in the expected time");

        Set<String> allIds = new HashSet<>();
        for (Future<Set<String>> future : futures) {
            Set<String> threadIds = future.get();
            for (String id : threadIds) {
                boolean isUnique = allIds.add(id);
                assertTrue(isUnique, "ID should be unique across all threads");
            }
        }

        assertEquals(numberOfThreads * idsPerThread, allIds.size(), "All generated IDs should be unique across all threads");
    }
}

