import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import org.springframework.aop.framework.AdvisedSupport;
import org.springframework.aop.TargetSource;
import org.springframework.lang.Nullable;

public class ExampleSpringServiceTest {

    private final ExampleSpringService service = new ExampleSpringService();

    // Helper class to allow proper equality checks based on TargetSource
    private static class TestAdvisedSupport extends AdvisedSupport {
        private final TargetSource targetSource;

        public TestAdvisedSupport(TargetSource targetSource) {
            this.targetSource = targetSource;
            this.setTargetSource(targetSource);
        }

        @Override
        public boolean equals(@Nullable Object obj) {
            if (this == obj) return true;
            if (!(obj instanceof TestAdvisedSupport)) return false;
            TestAdvisedSupport other = (TestAdvisedSupport) obj;
            if (this.targetSource == null && other.targetSource == null) {
                return true;
            }
            if (this.targetSource == null || other.targetSource == null) {
                return false;
            }
            return this.targetSource.equals(other.targetSource);
        }

        @Override
        public int hashCode() {
            return targetSource != null ? targetSource.hashCode() : 0;
        }
    }

    @Test
    public void testBothNull() {
        assertTrue(service.areProxyConfigsEquivalent(null, null), "Both configurations are null, should be equivalent.");
    }

    @Test
    public void testConfigAIsNull() {
        AdvisedSupport configB = new TestAdvisedSupport(new SimpleTargetSource(String.class, "TestTarget", true));
        assertFalse(service.areProxyConfigsEquivalent(null, configB), "Config A is null and Config B is not, should not be equivalent.");
    }

    @Test
    public void testConfigBIsNull() {
        AdvisedSupport configA = new TestAdvisedSupport(new SimpleTargetSource(String.class, "TestTarget", true));
        assertFalse(service.areProxyConfigsEquivalent(configA, null), "Config B is null and Config A is not, should not be equivalent.");
    }

    @Test
    public void testSameInstance() {
        AdvisedSupport config = new TestAdvisedSupport(new SimpleTargetSource(String.class, "TestTarget", true));
        assertTrue(service.areProxyConfigsEquivalent(config, config), "Both configurations are the same instance, should be equivalent.");
    }

    @Test
    public void testBothAdvisedSupportEquivalent() {
        TargetSource commonTargetSource = new SimpleTargetSource(String.class, "TestTarget", true);
        AdvisedSupport configA = new TestAdvisedSupport(commonTargetSource);
        AdvisedSupport configB = new TestAdvisedSupport(commonTargetSource);

        assertTrue(service.areProxyConfigsEquivalent(configA, configB), "Both AdvisedSupport configurations are equivalent.");
    }

    @Test
    public void testBothAdvisedSupportNotEquivalent() {
        AdvisedSupport configA = new TestAdvisedSupport(new SimpleTargetSource(String.class, "TestTargetA", true));
        AdvisedSupport configB = new TestAdvisedSupport(new SimpleTargetSource(Integer.class, 123, false));

        assertFalse(service.areProxyConfigsEquivalent(configA, configB), "AdvisedSupport configurations are not equivalent.");
    }

    @Test
    public void testOneAdvisedSupportOtherNot() {
        AdvisedSupport configA = new TestAdvisedSupport(new SimpleTargetSource(String.class, "TestTarget", true));
        Object configB = new Object();

        assertFalse(service.areProxyConfigsEquivalent(configA, configB), "One configuration is AdvisedSupport and the other is not, should not be equivalent.");
    }

    @Test
    public void testBothNonAdvisedSupportSameObject() {
        Object config = new Object();
        assertTrue(service.areProxyConfigsEquivalent(config, config), "Both configurations are the same non-AdvisedSupport object, should be equivalent.");
    }

    @Test
    public void testBothNonAdvisedSupportDifferentObjects() {
        Object configA = new Object();
        Object configB = new Object();
        assertFalse(service.areProxyConfigsEquivalent(configA, configB), "Both configurations are different non-AdvisedSupport objects, should not be equivalent.");
    }

    // Simple implementation of TargetSource for testing purposes
    private static class SimpleTargetSource implements TargetSource {
        private final Class<?> targetClass;
        private final Object target;
        private final boolean isStatic;

        public SimpleTargetSource(Class<?> targetClass, Object target, boolean isStatic) {
            this.targetClass = targetClass;
            this.target = target;
            this.isStatic = isStatic;
        }

        @Override
        public Class<?> getTargetClass() {
            return targetClass;
        }

        @Override
        public Object getTarget() {
            return target;
        }

        @Override
        public boolean isStatic() {
            return isStatic;
        }

        @Override
        public void releaseTarget(Object target) {
            // No-op
        }

        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (!(obj instanceof SimpleTargetSource)) return false;
            SimpleTargetSource other = (SimpleTargetSource) obj;
            return this.isStatic == other.isStatic &&
                   this.targetClass.equals(other.targetClass) &&
                   ((this.target == null && other.target == null) ||
                    (this.target != null && this.target.equals(other.target)));
        }

        @Override
        public int hashCode() {
            int result = targetClass.hashCode();
            result = 31 * result + (target != null ? target.hashCode() : 0);
            result = 31 * result + (isStatic ? 1 : 0);
            return result;
        }
    }
}

