import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.aop.framework.AdvisedSupport;

import java.io.Serializable;
import java.util.ArrayList;
import java.util.List;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private MockAdvisedSupport advisedSupportMock;
    private ExampleSpringService exampleSpringService;

    @BeforeEach
    public void setUp() {
        advisedSupportMock = new MockAdvisedSupport();
        exampleSpringService = new ExampleSpringService(advisedSupportMock);
    }

    @Test
    public void testUpdateProxyInterfaces_NoInterfaces() {
        exampleSpringService.updateProxyInterfaces();

        // Verify that removeInterface is never called
        assertTrue(advisedSupportMock.getRemovedInterfaces().isEmpty());
    }

    @Test
    public void testUpdateProxyInterfaces_SingleInterface() {
        Class<?> interface1 = Runnable.class;

        exampleSpringService.updateProxyInterfaces(interface1);

        // Verify that removeInterface is called once with Runnable.class
        List<Class<?>> removed = advisedSupportMock.getRemovedInterfaces();
        assertEquals(1, removed.size());
        assertEquals(interface1, removed.get(0));
    }

    @Test
    public void testUpdateProxyInterfaces_MultipleInterfaces() {
        Class<?> interface1 = Runnable.class;
        Class<?> interface2 = Comparable.class;
        Class<?> interface3 = Serializable.class;

        exampleSpringService.updateProxyInterfaces(interface1, interface2, interface3);

        // Verify that removeInterface is called once for each interface
        List<Class<?>> removed = advisedSupportMock.getRemovedInterfaces();
        assertEquals(3, removed.size());
        assertTrue(removed.contains(interface1));
        assertTrue(removed.contains(interface2));
        assertTrue(removed.contains(interface3));
    }

    @Test
    public void testUpdateProxyInterfaces_NullInterface() {
        // Assuming the method should handle null gracefully
        exampleSpringService.updateProxyInterfaces((Class<?>) null);

        // Verify that removeInterface is called once with null
        List<Class<?>> removed = advisedSupportMock.getRemovedInterfaces();
        assertEquals(1, removed.size());
        assertNull(removed.get(0));
    }

    @Test
    public void testUpdateProxyInterfaces_VerifyOrder() {
        Class<?> interface1 = Runnable.class;
        Class<?> interface2 = Comparable.class;

        exampleSpringService.updateProxyInterfaces(interface1, interface2);

        // Capture the order of method calls
        List<Class<?>> removed = advisedSupportMock.getRemovedInterfaces();
        assertEquals(2, removed.size());
        assertEquals(interface1, removed.get(0));
        assertEquals(interface2, removed.get(1));
    }

    // Mock implementation of AdvisedSupport
    private static class MockAdvisedSupport extends AdvisedSupport {
        private final List<Class<?>> removedInterfaces = new ArrayList<>();

        @Override
        public boolean removeInterface(Class<?> intf) {
            removedInterfaces.add(intf);
            return true;
        }

        public List<Class<?>> getRemovedInterfaces() {
            return removedInterfaces;
        }
    }
}

