import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.beans.BeansException;
import org.springframework.jndi.support.SimpleJndiBeanFactory;

import java.util.HashMap;
import java.util.Map;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private MockSimpleJndiBeanFactory mockBeanFactory;
    private ExampleSpringService exampleSpringService;

    @BeforeEach
    public void setUp() throws Exception {
        mockBeanFactory = new MockSimpleJndiBeanFactory();
        exampleSpringService = new ExampleSpringService();

        // Use reflection to set the private final beanFactory field
        java.lang.reflect.Field beanFactoryField = ExampleSpringService.class.getDeclaredField("beanFactory");
        beanFactoryField.setAccessible(true);
        beanFactoryField.set(exampleSpringService, mockBeanFactory);
    }

    @Test
    public void testFetchComponent_Success() {
        String componentId = "testComponent";
        TestComponent expectedComponent = new TestComponent();

        mockBeanFactory.addBean(componentId, expectedComponent);

        TestComponent actualComponent = exampleSpringService.fetchComponent(componentId, TestComponent.class);

        assertEquals(expectedComponent, actualComponent, "The fetched component should match the expected component.");
        assertEquals(1, mockBeanFactory.getGetBeanCallCount(componentId), "getBean should be called once.");
    }

    @Test
    public void testFetchComponent_ClassCastException() {
        String componentId = "testComponent";
        Object wrongTypeBean = new Object();

        mockBeanFactory.addBean(componentId, wrongTypeBean);

        RuntimeException exception = assertThrows(RuntimeException.class, () -> {
            exampleSpringService.fetchComponent(componentId, TestComponent.class);
        }, "Expected fetchComponent to throw RuntimeException due to ClassCastException.");

        assertTrue(exception.getCause() instanceof ClassCastException, "Cause should be ClassCastException.");
        assertEquals(1, mockBeanFactory.getGetBeanCallCount(componentId), "getBean should be called once.");
    }

    @Test
    public void testFetchComponent_BeansException() {
        String componentId = "nonExistentComponent";

        mockBeanFactory.setThrowOnGetBean(componentId, new BeansException("Bean not found") {});

        RuntimeException exception = assertThrows(RuntimeException.class, () -> {
            exampleSpringService.fetchComponent(componentId, TestComponent.class);
        }, "Expected fetchComponent to throw RuntimeException due to BeansException.");

        assertTrue(exception.getCause() instanceof BeansException, "Cause should be BeansException.");
        assertEquals(1, mockBeanFactory.getGetBeanCallCount(componentId), "getBean should be called once.");
    }

    // Mock implementation of SimpleJndiBeanFactory
    private static class MockSimpleJndiBeanFactory extends SimpleJndiBeanFactory {
        private final Map<String, Object> beans = new HashMap<>();
        private final Map<String, BeansException> exceptions = new HashMap<>();
        private final Map<String, Integer> getBeanCallCounts = new HashMap<>();

        public void addBean(String id, Object bean) {
            beans.put(id, bean);
        }

        public void setThrowOnGetBean(String id, BeansException exception) {
            exceptions.put(id, exception);
        }

        public int getGetBeanCallCount(String id) {
            return getBeanCallCounts.getOrDefault(id, 0);
        }

        @Override
        public Object getBean(String name) throws BeansException {
            getBeanCallCounts.put(name, getBeanCallCounts.getOrDefault(name, 0) + 1);
            if (exceptions.containsKey(name)) {
                throw exceptions.get(name);
            }
            if (beans.containsKey(name)) {
                return beans.get(name);
            }
            throw new BeansException("Bean not found: " + name) {};
        }
    }

    // Additional test class for a concrete type
    public static class TestComponent {
        private String name;

        public TestComponent() {
            this.name = "TestComponent";
        }

        public String getName() {
            return name;
        }

        // Override equals for assertion purposes
        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (!(obj instanceof TestComponent)) return false;
            TestComponent other = (TestComponent) obj;
            return this.name.equals(other.name);
        }

        @Override
        public int hashCode() {
            return name.hashCode();
        }
    }
}

